import re

def cleanWords(words):
    """This function rewrites the given string into positive/negative based on
    the given sentiment.

    Args:
        words (str): A string of text.

    Returns:
        (str): A string with positive/negative rewritten as needed.
    """
    match = re.search('negative',''.join(words.split()))
    if match:
        return re.sub(r'negative', 'negative', words)
    match = re.search('positive',''.join(words.split()))
    if match:
        return re.sub(r'positive', 'positive', words)

